import classes from "./QuoteItem.module.css";
import { Link } from "react-router-dom";

const dummyData = [
  {
    id: "q1",
    author: "suri",
    text: "who dares wins",
  },
  {
    id: "q2",
    author: "sura",
    text: "who dares wins always",
  },
  {
    id: "q3",
    author: "mamo",
    text: "who is it ",
  },
];

const QuoteItem = (props) => {
  return (
    <li className={classes.item}>
      <figure>
        <blockquote>
          <p>{props.text}</p>
        </blockquote>
        <figcaption>{props.author}</figcaption>
      </figure>
      <Link to={`/quotes/${dummyData.id}`} className="btn">
        View Fullscreen
      </Link>
    </li>
  );
};

export default QuoteItem;
