import { useRef, useState } from "react";
import { Prompt } from "react-router-dom";
import { Fragment } from "react";

import Card from "../UI/Card";
import LoadingSpinner from "../UI/LoadingSpinner";
import classes from "./QuoteForm.module.css";

const QuoteForm = (props) => {
  const [isEntered, setIsEntered] = useState(false);

  const authorInputRef = useRef();
  const textInputRef = useRef();
  const pdate = useRef();
  const fdate = useRef();
  const country = useRef();
  const region = useRef();

  function submitFormHandler(event) {
    event.preventDefault();

    const enteredAuthor = authorInputRef.current.value;
    const enteredText = textInputRef.current.value;
    const enteredpdate = pdate.current.value;
    const enteredfdata = fdate.current.value;
    const enteredcountry = country.current.value;
    const enteredregion = region.current.value;
    // optional: Could validate here
    props.onAddQuote({
      author: enteredAuthor,
      text: enteredText,
      pdate: enteredpdate,
      fdate: enteredfdata,
      country: enteredcountry,
      region: enteredregion,
    });
  }
  const formFocusHandler = () => {
    setIsEntered(true);
    console.log("focus");
  };

  const formReady = () => {
    setIsEntered(false);
  };

  return (
    <Fragment>
      <Prompt
        when={isEntered}
        message={(location) => "You sure want to leave this page"}
      />
      <Card>
        <form
          onFocus={formFocusHandler}
          className={classes.form}
          onSubmit={submitFormHandler}
        >
          {props.isLoading && (
            <div className={classes.loading}>
              <LoadingSpinner />
            </div>
          )}

          <div className={classes.control}>
            <label htmlFor="author">Author</label>
            <input type="text" id="author" ref={authorInputRef} />
          </div>
          <div className={classes.control}>
            <label htmlFor="author">Publication Date</label>
            <input type="date" id="publicationdate" ref={pdate} />
          </div>
          <div className={classes.control}>
            <label htmlFor="author">Tender floating Date</label>
            <input type="date" id="date" ref={fdate} />
          </div>

          <div className={classes.control}>
            <label htmlFor="text">Country</label>
            <textarea id="text" ref={countryInputRef}></textarea>
          </div>
          <div className={classes.control}>
            <label htmlFor="text">Region</label>
            <textarea id="text" ref={regionInputRef}></textarea>
          </div>

          <div className={classes.control}>
            <label htmlFor="text">Tender specification</label>
            <textarea id="text" rows="5" ref={textInputRef}></textarea>
          </div>

          <div className={classes.actions}>
            <button onClick={formReady} className="btn">
              Add Tender Quote
            </button>
          </div>
        </form>
      </Card>
    </Fragment>
  );
};

export default QuoteForm;
