import React from "react";
import { useParams, Route } from "react-router-dom";
import Comments from "../components/comments/Comments";

import HighlightedQuote from "../components/quotes/HighlightedQuote";

const dummyData = [
  {
    id: "q1",
    author: "suri",
    text: "who dares wins",
  },
  {
    id: "q2",
    author: "sura",
    text: "who dares wins always",
  },
  {
    id: "q3",
    author: "mamo",
    text: "who is it ",
  },
];

function Away() {
  const params = useParams();

  const quote = dummyData.find((quote) => quote.id === params.id);

  return (
    <section>
      <h1> Specific page Quote detail with id</h1>
      <h2>{params.id}</h2>
      {/* Nested Routes for a specific page  */}
      <Route path="/quotes/:id/comments">
        <Comments />
      </Route>
    </section>
  );
}

export default Away;
