// Quote Detail pages

import { useEffect } from "react";
import { useParams, Route, Link, useHistory } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";

import useHttp from "../components/hooks/use-http";
import { addComment } from "../components/lib/api";

const dummyData = [
  {
    id: "q1",
    author: "CBE",
    text: "Techincal support ",
  },
  {
    id: "q2",
    author: "EEEPL",
    text: "Installation",
  },
  {
    id: "q3",
    author: "Telecommincation",
    text: "Tender Document",
  },
];

function Away() {
  const history = useHistory();
  const params = useParams();
  const { sendRequest, status } = useHttp(addComment);

  useEffect(() => {
    if (status === "completed") history.pushState("/");
  }, [status, history]);

  const quote = dummyData.find((quote) => quote.id === params.id);
  if (!quote) {
    return <p> No quote with such Id</p>;
  }

  return (
    <section>
      <HighlightedQuote text={quote.text} author={quote.author} />
      <Route path={`/quotes/${params.id}`} exact>
        <div className="centered">
          <Link className="btn--flat" to={`/quotes/${params.id}/comments`}>
            To comments
          </Link>
        </div>
      </Route>
      {/* <h1> Specific page Quote detail with id</h1>
      <h2>{params.id}</h2> */}
      {/* Nested Routes for a specific page  */}
      <Route path={`/quotes/${params.id}/comments`}>
        <Comments />
      </Route>
    </section>
  );
}

export default Away;
