import { Fragment } from "react";
import { useLocation, useHistory } from "react-router-dom";

import QuoteItem from "./QuoteItem";
import classes from "./QuoteList.module.css";

const QuoteList = (props) => {
  const history = useHistory();
  const location = useLocation();
  // sorting using query parameters: which is optional

  const queryParams = new URLSearchParams(location.search);
  const isSortAscending = queryParams.get("sort") === "asc"; // what is the value in there.

  const sortingHandler = () => {
    history.push("/quotes?sort=asc"); // this action re-renders the page
    const status = history.location;
    console.log(status);
  };

  return (
    <Fragment>
      {/*the sort button   */}
      <div className={classes.sorting}>
        <button onClick={sortingHandler}>
          Sort {isSortAscending ? "Decending" : "Ascending"}
        </button>
      </div>
      <ul className={classes.list}>
        {props.quotes.map((quote) => (
          <QuoteItem
            key={quote.id}
            id={quote.id}
            author={quote.author}
            text={quote.text}
          />
        ))}
      </ul>
    </Fragment>
  );
};

export default QuoteList;
